import { AdminService } from 'src/app/services/admin.service';
import { Component, OnInit } from '@angular/core';
import { ProductModel } from 'src/app/models/product';
import { Router } from '@angular/router';
import { CategoryModel } from 'src/app/models/category';

@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css']
})
export class AddProductComponent implements OnInit {

    public categories: CategoryModel[] = [];
    public product = new ProductModel();

  constructor(private adminService: AdminService, private router: Router ) { }

 async ngOnInit(){
    try{
        this.categories = await this.adminService.getAllCategories();
    }
    catch(err: any){
        alert(err.message);
        }
  }

    public async send(){
        try{
            await this.adminService.addProduct(this.product);
            alert('product has been added!');
            this.router.navigateByUrl("/admin");
        }
        catch(err: any){
            alert(err.message);
        }
    }
}
<div>

    <form (ngSubmit)="send()" ngNativeValidate>

        <label>Select Area: </label>
        <select name="categoryId" [(ngModel)]="product.categoryId" required>
            <option selected disabled>Select Category</option>
            <option *ngFor="let c of categories" [value]="c._id">{{c.name}}</option>
        </select>

        <label>Name: </label>
        <input type="text" name="name" [(ngModel)]="product.name"  #nameControl="ngModel" required minlength="3" maxlength="100" />
        <span *ngIf="nameControl.errors?.['required'] && nameControl.touched">Missing name</span>
        <span *ngIf="nameControl.errors?.['minlength'] && nameControl.touched">Name too short</span>

        <label>Price: </label>
        <input type="number" name="price" [(ngModel)]="product.price" required />

        <label>Image: </label>


        <button>Add</button>

    </form>

</div>
