import { Component, OnInit } from '@angular/core';
import { UserModel } from 'src/app/models/user';
import { ProductsService } from 'src/app/services/products.service';
import { ProductModel } from 'src/app/models/product';
import { AdminService } from 'src/app/services/admin.service';
import { store } from 'src/app/redux/store';
import { Router } from '@angular/router';


@Component({
    selector: 'app-admin',
    templateUrl: './admin.component.html',
    styleUrls: ['./admin.component.css']
})
export class AdminComponent implements OnInit {
    public user: UserModel;
    public products: ProductModel[];
    public product: ProductModel = new ProductModel();
    public categories: any = [];
    public newProduct: boolean = false;
    public editProduct: boolean = false;

    constructor(private myProductsService: ProductsService, private adminService: AdminService,private router: Router) { }

    async ngOnInit() {
        try{ 
        store.subscribe(() => {
            this.user = store.getState().user;
        });
         this.products = await this.myProductsService.getAllProducts()

         this.categories = await this.adminService.getAllCategories()    
    }
    catch(err: any){
        alert(err.message);
    }
    }

    // public addProduct(): void {
    //     if (!this.product.img) {
    //         alert('You have to upload image !');
    //         return;
    //     }
    //     const myForm: FormData = new FormData();
    //     myForm.append('image', this.product.img);
    //     myForm.append('product', JSON.stringify(this.product));

    //     this.adminService.addProduct(myForm)
    //         .subscribe(res => {
    //             this.products.push(res);
    //             alert('product has been added !');
    //             this.goBack();
    //         }, err => alert(err.message));
    // }

    public async addProduct(){
        try{
            this.adminService.addProduct(this.product);
            alert('product has been added !');
            this.router.navigateByUrl("/admin");
        }
        catch(err:any){
            alert(err.message);
        }
    }




    public updateImg(event: any): void {
        this.product.img = event.target.files[0];
    }

    public goToEditProduct(_id: string): void {
        this.editProduct = true;
        const selectedProduct = this.products.find(p => p._id === _id);
        this.product = selectedProduct;
    }

    public goToAddProduct(): void {
        this.newProduct = true;
    }



    // public updateProduct(): void {
    //     const myForm: FormData = new FormData();
    //     myForm.append('image', this.product.img);
    //     myForm.append('product', JSON.stringify(this.product));

    //     this.adminService.updateProduct(myForm)
    //         .subscribe(res => {
    //             const index = this.products.findIndex(product => product._id === res._id);
    //             this.products[index] = res;
    //             alert('product has been updated!');
    //             this.goBack();
    //         }, err => alert(err.message))
    // }

    public async updateProduct(){
        try{
            const myForm: FormData = new FormData();
            myForm.append('image', this.product.img);
            const product: ProductModel = myForm.append('product', JSON.stringify(this.product));
            this.adminService.updateProduct(product)


            this.adminService.updateProduct(this.product);
            alert('product has been updated!');
            this.goBack();
        }
        catch(err:any){
            alert(err.message);
        }
    }


    public goBack(): void {
        this.newProduct = false;
        this.editProduct = false;
        this.product = new ProductModel();
    }
}
