import { environment } from './../../environments/environment';
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { ProductModel } from '../models/product';

@Injectable({
  providedIn: 'root'
})
export class ProductsService {
 
  constructor(private http: HttpClient) { }

  public async getAllProducts(): Observable<ProductModel[]> {
    
    return this.http.get<ProductModel[]>(environment.productsUrl + "get-all-products");
  }
  public searchProduct(name: string): Observable<ProductModel[]> {
    return this.http.get<ProductModel[]>(environment.productsUrl + "search-product/" + name);
  }
}
