import { AdminService } from 'src/app/services/admin.service';
import { Component, OnInit } from '@angular/core';
import { ProductModel } from 'src/app/models/product';
import { Router } from '@angular/router';

@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css']
})
export class AddProductComponent implements OnInit {

    public categories: any = [];
    public product = new ProductModel();

  constructor(private adminService: AdminService, private router: Router ) { }

  ngOnInit(){
    try{
        this.categories = this.adminService.getAllCategories();
    }
    catch(err: any){
        alert(err.message);
        }
  }

    public async send(){
        try{
            await this.adminService.addProduct(this.product);
            this.router.navigateByUrl("/admin");
        }
        catch(err: any){
            alert(err.message);
        }
    }
}
<div>

    <form (ngSubmit)="send()" ngNativeValidate>

        <label>Select Area: </label>
        <select name="areaId" [(ngModel)]="site.areaId" required>
            <option selected disabled>Select Area</option>
            <option *ngFor="let a of areas" [value]="a._id">{{a.area}}</option>
        </select>

        <label>Name: </label>
        <input type="text" name="name" [(ngModel)]="site.name" required minlength="3" maxlength="100" />

        <label>Description: </label>
        <input type="text" name="description" [(ngModel)]="site.description" required minlength="3" maxlength="100" />

        <label>Adult Price: </label>
        <input type="number" name="adultPrice" [(ngModel)]="site.adultPrice" required min="0" max="1000" />

        <label>Child Price: </label>
        <input type="number" name="childPrice" [(ngModel)]="site.childPrice" required min="0" max="1000" />

        <button>Add</button>

    </form>

</div>
