import { ProductModel } from 'src/app/models/product';
import { environment } from './../../environments/environment.prod';
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';


@Injectable({
    providedIn: 'root'
})
export class AdminService {

    constructor(private http: HttpClient) { }

     public getAllCategories() {
        return this.http.get(environment.adminUrl + 'get-all-categories');

    }
    public async addProduct(product: ProductModel): Promise<ProductModel> {
        const observable = this.http.post<ProductModel>(environment.adminUrl + 'add-product', product);
    }
    public updateProduct(product: any) {
        return this.http.put<any>(environment.adminUrl + 'update-product', product);
    }
}
